// === Action Dispatch Helper ===

fn dispatch_event(comp: &mut ToggleGroup, event: &str) {
    match event {
            "SELECTITEM" => { let _ = comp.select_item(); }
            "FOCUSNEXTITEM" => { let _ = comp.focus_next_item(); }
            "FOCUSPREVITEM" => { let _ = comp.focus_prev_item(); }
            "FOCUSFIRSTITEM" => { let _ = comp.focus_first_item(); }
            "FOCUSLASTITEM" => { let _ = comp.focus_last_item(); }
            "ACTIVATEITEM" => { let _ = comp.activate_item(); }
            "RESETFOCUS" => { let _ = comp.reset_focus(); }
            _ => eprintln!("Unknown event: {}", event),
    }
}

